mapper.insert:
INSERT INTO post(board_id, post_id, title, display_name, contents, created_date_time)
VALUES (
        #{boardId},
           COALESCE(
                   (SELECT MAX(post_id) + 1 FROM post WHERE board_id = #{boardId}), 1
               )
       , #{title}
       , #{displayName}
       , #{contents}
       , CURRENT_TIMESTAMP
       );

mapper.delete:
DELETE
FROM    post
where   board_id = #{boardId}
AND     post_id  = #{postId}
